package org.usfirst.frc2855.cool2017.commands;

import edu.wpi.first.wpilibj.command.Command;
import org.usfirst.frc2855.cool2017.Robot;

/**
 * spins motor for ball intake mechanism
 * can spin at different speeds/directions based on the value sent from oi
 * when the command is created
 */
public class IntakeControl extends Command {


    private double m_intakeSpeed;
    
    public IntakeControl(double intakeSpeed) {

        m_intakeSpeed = intakeSpeed;

        requires(Robot.ballintake);

    }

    // Called just before this Command runs the first time
    protected void initialize() {
    }

    // Called repeatedly when this Command is scheduled to run
    protected void execute() {
    	Robot.ballintake.grabBall(m_intakeSpeed);
    	Robot.leds.LEDIntakeActivate();
    }

    // Make this return true when this Command no longer needs to run execute()
    protected boolean isFinished() {
        return false;
    }

    // Called once after isFinished returns true
    protected void end() {
    	Robot.ballintake.grabBall(0);//stop motor
    	Robot.leds.LEDIntakeDeActivate();
    }

    // Called when another command which requires one or more of the same
    // subsystems is scheduled to run
    protected void interrupted() {
    	end();
    }
}
